mapper.select:
select eid, create_time, update_time, role_name, service, description
from gambling.auth_role
where role_name = #{roleName} and service = #{service}

select * from ( select eid, create_time, update_time, role_name, service, description, ROWNUM rn from gambling.auth_role where 1=1
and role_name like '%'|| #{name} ||'%'
and description like '%'|| #{description} ||'%'
and service like '%'|| #{service} ||'%'
) where rn between #{pageStart} and #{pageEnd}
