mapper.select:
select eid, create_time, update_time, user_id, user_name, password, status, login_fail
from gambling.auth_user
where user_id = #{userId}

select * from (
    select A.*, rownum AS rn from (
        select eid, create_time, update_time, user_id, user_name, status, login_fail
        from gambling.auth_user where 1=1

            AND (USER_ID like '%'||#{ value }||'%' or USER_NAME like '%'||#{ value }||'%')

    ) A
) where rn<=#{count}
